import React, { useState } from "react";

const App = () => {
  //   let date = new Date().toLocaleTimeString();
  const state = useState();
  const [date, setCount] = useState("Hit the button to know current time");
  const IncTheNum = () => {
    setCount(new Date().toLocaleTimeString());
    console.log("Clicked");
  };
  return (
    <>
      <h1>{date}</h1>
      <button onClick={IncTheNum}>Click</button>
    </>
  );
};
export default App;
