import React, { useState } from "react";

const App = () => {
  const state = useState();
  const [count, setCount] = useState(0);
  const IncTheNum = () => {
    setCount(count + 1);
    console.log("Clicked");
  };
  return (
    <>
      <h1>{count}</h1>
      <button onClick={IncTheNum}>Click</button>
    </>
  );
};
export default App;
